// Helper function to get vouchers by category
export const getVouchersByCategory = (category) => {
  if (category === "All") {
    return sampleVouchers;
  }
  return sampleVouchers.filter(voucher => voucher.category === category);
};

// Helper function to search vouchers
export const searchVouchers = (searchTerm) => {
  const term = searchTerm.toLowerCase();
  return sampleVouchers.filter(voucher => 
    voucher.title.toLowerCase().includes(term) ||
    voucher.description.toLowerCase().includes(term) ||
    voucher.category.toLowerCase().includes(term)
  );
};

// Helper function to get voucher by ID
export const getVoucherById = (id) => {
  return sampleVouchers.find(voucher => voucher.id === parseInt(id));
};

// Helper function to get voucher by slug
export const getVoucherBySlug = (slug) => {
  const title = slug.split('-').map(word => 
    word.charAt(0).toUpperCase() + word.slice(1)
  ).join(' ');
  
  return sampleVouchers.find(voucher => 
    voucher.title.toLowerCase().replace(/[^a-z0-9]+/g, '-') === slug
  );
}; 